    int PassId = 0;
    
    private void CountPassenger(){
        try {
            St1 = Con.createStatement();
            Rs1 = St1.executeQuery("select Max(pass_id) from PassengerRegisterTbl");
            Rs1.next();
            PassId = Rs1.getInt(1)+1;
        } catch (Exception e) {
            
        }
    }
    
    
    public boolean checkUser(String username) throws SQLException{
                String query = "SELECT * FROM PassengerRegisterTbl WHERE pass_username =?";
                boolean checkUser = false;
                Con = DriverManager.getConnection("jdbc:mysql://localhost:3308/AirlineDb","root","");
                Pst = Con.prepareStatement(query);
            
                Pst.setString(1, username);
                
                Rs = Pst.executeQuery();
                
                if(Rs.next()){
                    checkUser = true;
                }
                
                return checkUser;

    }
    
    public boolean checkPassport(String email) throws SQLException{
                String query = "SELECT * FROM PassengerRegisterTbl WHERE pass_passport =?";
                boolean checkPassport = false;
                Con = DriverManager.getConnection("jdbc:mysql://localhost:3308/AirlineDb","root","");
                Pst = Con.prepareStatement(query);
            
                Pst.setString(1, email);
                
                Rs = Pst.executeQuery();
                
                if(Rs.next()){
                    checkPassport = true;
                }
                
                return checkPassport;

    }
    
    public boolean checkPhone(String phone) throws SQLException{
                String query = "SELECT * FROM PassengerRegisterTbl WHERE pass_phone =?";
                boolean checkPhone = false;
                Con = DriverManager.getConnection("jdbc:mysql://localhost:3308/AirlineDb","root","");
                Pst = Con.prepareStatement(query);
            
                Pst.setString(1, phone);
                
                Rs = Pst.executeQuery();
                
                if(Rs.next()){
                    checkPhone = true;
                }
                
                return checkPhone;

    }
    
    
    
    
    
    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        try {
                          if(checkUser(PRusername.getText())){
                              JOptionPane.showMessageDialog(null, "This username already exist");
                          }
                          else if(checkPassport(PRemail.getText())){
                              JOptionPane.showMessageDialog(null, "This passport number already exist");
                          }
                          else if(checkPhone(PRphone.getText())){
                              JOptionPane.showMessageDialog(null, "This phone number already exist");
                          }
                          else{
                          CountPassenger();
                          Con = DriverManager.getConnection("jdbc:mysql://localhost:3308/AirlineDb","root","");
                          PreparedStatement Add = Con.prepareStatement("insert into PassengerRegisterTbl values(?,?,?,?,?,?,?,?,?,?,?)");
                          Add.setInt(1, PassId);
                          Add.setString(2, PRusername.getText());
                          Add.setString(3, PRemail.getText());
                          Add.setString(4, PRname.getText());
                          Add.setString(5, PRsurname.getText());
                          Add.setString(6, PRdate.getDate().toString());
                          Add.setString(7, PRgender.getSelectedItem().toString());
                          Add.setString(8, PRphone.getText());
                          Add.setString(9, PRpassword.getText());
                          Add.setString(10, PRnationality.getSelectedItem().toString());
                          Add.setString(11, PRaddress.getText());
                          int row=Add.executeUpdate();
                          JOptionPane.showMessageDialog(this, "Passenger created");
                          Con.close();
                          Clear();
                          }
                          
           } catch (Exception e) {
               JOptionPane.showMessageDialog(this, e);
           }
    }//GEN-LAST:event_jButton1MouseClicked

